    #region Part 1

    [TestMethod]
    public void CalculateGpsCoordinatesSum_SmallSampleWarehouse_PerformsCorrectly()
    {
        // Arrange
        Day15WarehouseWoes day15WarehouseWoes = new();
        var warehouse = _getSmallSampleWarehouse();
        var moves = _getSmallSampleMoves();

        // Act
        var actual = day15WarehouseWoes.CalculateGpsCoordinatesSum(warehouse, moves);

        // Assert
        actual.Should().Be(2028);
    }

    [TestMethod]
    public void CalculateGpsCoordinatesSum_LargeSampleWarehouse_PerformsCorrectly()
    {
        // Arrange
        Day15WarehouseWoes day15WarehouseWoes = new();
        var warehouse = _getLargeSampleWarehouse();
        var moves = _getLargeSampleMoves();

        // Act
        var actual = day15WarehouseWoes.CalculateGpsCoordinatesSum(warehouse, moves);

        // Assert
        actual.Should().Be(10092);
    }

    [TestMethod]
    public void CalculateGpsCoordinatesSum_TestWarehouse_PerformsCorrectly()
    {
        // Arrange
        Day15WarehouseWoes day15WarehouseWoes = new();
        var warehouse = _getTestWarehouse();
        var moves = _getTestMoves();

        // Act
        var actual = day15WarehouseWoes.CalculateGpsCoordinatesSum(warehouse, moves);

        // Assert
        actual.Should().Be(1414416);
    }

    #endregion

    #region Part 2

    [TestMethod]
    public void CalculateExtendedGpsCoordinatesSum_LargeSampleWarehouse_PerformsCorrectly()
    {
        // Arrange
        Day15WarehouseWoes day15WarehouseWoes = new();
        var warehouse = _getLargeSampleWarehouse();
        var moves = _getLargeSampleMoves();

        // Act
        var actual = day15WarehouseWoes.CalculateExtendedGpsCoordinatesSum(warehouse, moves);

        // Assert
        actual.Should().Be(9021);
    }

    [TestMethod]
    public void CalculateExtendedGpsCoordinatesSum_TestWarehouse_PerformsCorrectly()
    {
        // Arrange
        Day15WarehouseWoes day15WarehouseWoes = new();
        var warehouse = _getTestWarehouse();
        var moves = _getTestMoves();

        // Act
        var actual = day15WarehouseWoes.CalculateExtendedGpsCoordinatesSum(warehouse, moves);

        // Assert
        actual.Should().Be(1386070);
    }

    #endregion

